import * as ns_1 from './1.cjs';
import * as ns_2 from './2.cjs';
import * as ns_3 from './3.cjs';
import * as ns_4 from './4.cjs';
import * as ns_5 from './5.cjs';
import * as ns_6 from './6.cjs';
import * as ns_7 from './7.cjs';
import * as ns_8 from './8.cjs';

export const results = [ns_1, ns_2, ns_3, ns_4, ns_5, ns_6, ns_7, ns_8];
const has = (obj, key) => Object.prototype.hasOwnProperty.call(obj, key);
const checks = {
  'have a default export': ns => has(ns, 'default'),
  'to have a named export called even': ns => ns.even,
  'to have even field in default export': ns =>
    !!(ns && ns.default && ns.default.even),
};

results.map((namespace, index) =>
  Object.keys(checks).map(name => {
    if (!checks[name](namespace)) {
      throw Error(`Expected ${index + 1}.cjs to ${name}`);
    }
  }),
);
